	def listOfPlayersWithMoreThan1YellowCard = []

	for( player in listOfPlayersWithYellowCard )
	{
		if((listOfPlayersWithYellowCard.count("$player")) > 1) {
		listOfPlayersWithMoreThan1YellowCard.add("$player")
		}
	}
	log.info listOfPlayersWithMoreThan1YellowCard.size()
	return listOfPlayersWithMoreThan1YellowCard
}

