def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder("getAllPlayersWithYellowOrRedCards#Response")

holder.namespaces["ns"] = "http://footballpool.dataaccess.eu"

def yellowCards = holder.getNodeValues( "//ns:tPlayersWithCards/ns:iYellowCards")
def redCards = holder.getNodeValues( "//ns:tPlayersWithCards/ns:iRedCards")

def getNumberOfCards(cards) {
	def numberOfCards = 0

	for( number in cards )
	{
		numberOfCards += number.toInteger()
	}
	log.info numberOfCards
	return numberOfCards
}

def numberOfYellowCards = getNumberOfCards(yellowCards)
def numberOfRedCards = getNumberOfCards(redCards)

def numberOfTotalCards = numberOfYellowCards + numberOfRedCards
log.info numberOfTotalCards
testRunner.testCase.setPropertyValue("totalNumberOfCards", "$numberOfTotalCards")

con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( "getTotalNumberOfCards#Response" )

holder.namespaces["ns"] = "http://footballpool.dataaccess.eu"
def yellowCards = holder.getNodeValues( "//ns:YellowAndRedCardsTotalResult/ns:iYellow" )[0].toInteger()
def redCards = holder.getNodeValues( "//ns:YellowAndRedCardsTotalResult/ns:iRed" )[0].toInteger()

def totalNumberOfCards = yellowCards + redCards
def totanlNumberOfCardsFromProperty = messageExchange.modelItem.testStep.testCase.getPropertyValue( "totalNumberOfCards" ).toInteger()

assert  totalNumberOfCards == totanlNumberOfCardsFromProperty
